/* ./src/index.css */
/*@tailwind base;*/
/*@tailwind components;*/
/*@tailwind utilities;*/


@import 'primereact/resources/themes/tailwind-light/theme.css';
@import 'primeflex/primeflex.min.css';
@import 'primeicons/primeicons.css';

.exchangeCalculator-dialog {
    width: 1110px;
}

.exchangeCalculator-currency {
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    gap: 8px;
    justify-self: center;
    justify-content: center;
}

.exchangeCalculator-currency-flag {
    width: 32px;
    height: 24px;
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    border-radius: 1px;
}

.exchangeCalculator-currency-text {
    font-style: normal;
    font-weight: 600;
    font-size: 17px;
    line-height: 24px;
    letter-spacing: -0.408px;
    color: #19181b;
}

.exchangeCalculator-currencyMobile {
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    gap: 8px;
    align-items: center;
}

.exchangeCalculator-currencyMobile-description {
    justify-content: left;
}

.exchangeCalculator-currencyMobile-text {
    font-family: ' Segoe UI Semi Bold ';
    font-style: normal;
    font-weight: 600;
    font-size: 10px;
    line-height: 14px;
    color: #19181b;
    text-align: left;
    margin: 0px !important;
}

.exchangeCalculator-currencyMobile-shortName {
    font-family: ' Segoe UI ';
    font-style: normal;
    font-weight: 400;
    font-size: 10px;
    line-height: 14px;
    color: rgba(25, 24, 27, 0.64);
    text-align: left;
    margin: 0px !important;
}

.exchangeCalculator-currencyMobile-flag {
    width: 24px;
    height: 16px;
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    border-radius: 1px;
}

.exchangeCalculator-rate {
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    flex-wrap: wrap;
    gap: 5px;
}

.exchangeCalculator-title {
    margin: 0;
    font-family: ' Segoe UI ';
    font-style: normal;
    font-weight: 600;
    font-size: 22px;
    line-height: 32px;
    letter-spacing: 0.36px;
    color: #19181b;
    padding: 0 !important;
}

.exchangeCalculator-header {
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
}

.exchangeCalculator-header-calendar {
    padding: 6px 16px;
    border: 1px solid #024e31;
    border-radius: 2px;
}

.exchangeCalculator-header-calendar .p-button {
    background: none;
    border: none;
    padding: 0 !important;
}

.exchangeCalculator-header-calendar .p-button:enabled:hover {
    background: none;
    border: none;
}

.exchangeCalculator-header-calendar .p-button.p-button-icon-only {
    width: 12px;
    align-self: center;
}

.exchangeCalculator-header-calendar .p-inputtext {
    padding: 0 !important;
    width: 73px !important;
    height: 20px !important;
    border-radius: 0;
    font-family: ' Segoe UI ';
    font-style: normal;
    font-weight: 600;
    font-size: 13px;
    line-height: 20px;
    letter-spacing: 0.5px;
    color: #19181b;
    margin-right: 12px;
    align-self: center;
}

.exchangeCalculator-header-date {
    border: 1px solid #024e31;
    border-radius: 2px;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    flex-wrap: wrap;
    gap: 12px;
    padding: 6px 16px;
    cursor: pointer;
}

.exchangeCalculator-header-date p {
    margin: 0;
    font-family: ' Segoe UI ';
    font-style: normal;
    font-weight: 600;
    font-size: 13px;
    line-height: 20px;
    display: flex;
    align-items: center;
    text-align: center;
    letter-spacing: 0.5px;
    color: #19181b;
    user-select: none;
}

.exchangeCalculator-table {
    width: 100%;
    padding: 30px 0;
}

.exchangeCalculator-table-backgroundWhite {
    background-color: #fff !important;
}

.exchangeCalculator-table-backgroundGrey {
    background-color: #f9f9f9 !important;
}

.exchangeCalculator-table .p-column-header-content {
    justify-content: center !important;
    background-color: #f9f9f9 !important;
}

.exchangeCalculator-table-midRate {
    margin: 0;
    font-family: ' Segoe UI Semi Bold ' !important;
    font-style: normal !important;
    font-weight: 600 !important;
    font-size: 10px !important;
    line-height: 14px !important;
    text-align: center !important;
    color: #19181b !important;
    align-self: center !important;
    justify-self: center;
}

.exchangeCalculator-table-title {
    margin: 0;
    font-family: ' Segoe UI ' !important;
    font-style: normal !important;
    font-weight: 600 !important;
    font-size: 10px !important;
    line-height: 14px !important;
    text-align: center !important;
    color: rgba(25, 24, 27, 0.4) !important;
    align-self: center !important;
    justify-self: center;
}

.exchangeCalculator-table-mobile {
    display: none;
}

.exchangeCalculator-table-web {
    display: flex;
}

.exchangeCalculator-table-web .p-datatable .p-datatable-thead >
tr >
th {
    border: 1px solid rgba(0, 0, 0, 0.12);
}

.exchangeCalculator-table-web .p-datatable .p-datatable-thead >
tr:nth-child(1) .p-column-header-content {
    background-color: #f9f9f9 !important;
}

.exchangeCalculator-table-web .p-datatable .p-datatable-thead >
tr:nth-child(1) >
th {
    font-family: ' Segoe UI Semi Bold ';
    font-style: normal;
    font-weight: 600;
    font-size: 17px;
    line-height: 24px;
    letter-spacing: -0.408px;
    color: #19181b;
}

.exchangeCalculator-table-web .p-datatable .p-datatable-thead >
tr:nth-child(2) .p-column-header-content {
    background-color: transparent !important;
}

.exchangeCalculator-table-web .p-datatable .p-datatable-thead >
tr:nth-child(2) >
th {
    font-family: ' Segoe UI ';
    font-style: normal;
    font-weight: 400;
    font-size: 15px;
    line-height: 20px;
    letter-spacing: -0.24px;
    color: #19181b;
    background-color: transparent;
}

.exchangeCalculator-table-web .p-datatable .p-datatable-tbody >
tr >
td {
    background-color: transparent;
    border: 1px solid rgba(0, 0, 0, 0.12);
    border-top: none;
    border-bottom: none;
    text-align: center !important;
    justify-content: center !important;
    align-self: center !important;
    font-family: ' Segoe UI ' !important;
    font-style: normal !important;
    font-weight: 400 !important;
    font-size: 17px !important;
    line-height: 24px !important;
    letter-spacing: -0.408px !important;
    color: #19181b !important;
    vertical-align: inherit !important;
}

.exchangeCalculator-description {
    font-family: ' Segoe UI ';
    font-style: normal;
    font-weight: 400;
    font-size: 12px;
    line-height: 16px;
    display: flex;
    align-items: center;
    color: #19181b;
}

.exchangeCalculator-email {
    font-family: ' Segoe UI ';
    font-style: normal;
    font-weight: 400;
    font-size: 12px;
    line-height: 16px;
    display: flex;
    align-items: center;
    color: #19181b;
}

.exchangeCalculator-type {
    font-family: ' Segoe UI ';
    font-style: normal;
    font-weight: 400;
    font-size: 15px;
    line-height: 20px;
    letter-spacing: -0.24px;
    color: #19181b;
    margin: 0;
}

.exchangeCalculator-typeCheck {
    width: 24px;
    height: 24px;
    border-radius: 50%;
    background-color: rgba(0, 0, 0, 0.08);
    cursor: pointer;
    margin-right: 8px;
}

.exchangeCalculator-typeCheck-active {
    border: 6px solid #024e31;
}

.exchangeCalculator-typeCheck:hover {
    border: 6px solid #024e31;
}

.exchangeCalculator-transaction {
    margin-top: 30px !important;
    flex-wrap: wrap;
    gap: 10px;
}

.exchangeCalculator-onlineRate {
    margin-bottom: 30px !important;
}

.exchangeCalculator-onlineRate-text {
    font-family: ' Segoe UI ';
    font-style: normal;
    font-weight: 400;
    font-size: 17px;
    line-height: 24px;
    letter-spacing: -0.408px;
    color: #19181b;
}

.exchangeCalculator-onlineRate-button {
    height: 48px;
    padding: 12px !important;
    background: #024e31 !important;
    border: none !important;
    border-radius: 4px !important;
    font-family: ' Segoe UI Semi Bold ' !important;
    font-style: normal;
    font-weight: 700 !important;
    font-size: 15px !important;
    line-height: 24px;
    letter-spacing: 1px;
    color: #fff !important;
}

.exchangeCalculator-onlineRate-button:enabled:hover {
    background-color: #024e31;
}

.exchangeCalculator-container {
    background-color: #f9f9f9;
    margin-left: -1.5rem;
    margin-right: -1.5rem;
    padding: 1.5rem;
}

.exchangeCalculator-arrow {
    height: 56px;
    width: 56px;
    border-radius: 50%;
    background-color: #f2f2f5;
    display: flex;
    justify-content: center;
    align-items: center;
}

.exchangeCalculator-input {
    background-color: #fff;
    display: flex;
    align-items: center;
}

.exchangeCalculator-input-value {
    height: 56px;
    flex: 2;
    font-family: ' Segoe UI ';
    font-style: normal;
    font-weight: 400;
    font-size: 17px;
    line-height: 24px;
    letter-spacing: -0.408px;
    color: rgba(25, 24, 27, 0.64);
}

.exchangeCalculator-input-line {
    border: 1px solid rgba(0, 0, 0, 0.12);
    height: 38px;
}

.exchangeCalculator-input-currency {
    height: 56px;
    flex: 1;
}

.exchangeCalculator-input-currency span {
    align-self: center;
}

.exchangeCalculator-input-currency .p-dropdown-label .p-inputtext {
    border: 1px solid red !important;
